// Ambil id dari URL
$id = isset($_GET['id']) ? intval($_GET['id']) : 0;

// Cek ID valid
if ($id <= 0) {
    die("ID Hotel tidak valid.");
}

// Query hotel
$q = mysqli_query($koneksi, "SELECT * FROM hotel WHERE id_hotel = $id");
$hotel = mysqli_fetch_assoc($q);

// Jika tidak ditemukan
if (!$hotel) {
    die("<h2>Hotel tidak ditemukan.</h2>");
}
